#include<iostream>using namespace std;int main(){	int *p, i, a = 0, b = 0, c = 0, d = 0, sum = 0, t = 0, f = 0;	p = new int[20];	for (i = 0; i < 20; i++)	{		cin >> p[i];		if (p[i]> 0)			a++;		if (p[i]< 0)			b++;	}	for (i = 0; i < 20; i++)	{		sum = p[i] + sum;	}	t = sum / 20;	for (i = 0; i < 20; i++)	{		f = f + (p[i] - t) / 20 * (p[i] - t) / 20;	}	for (c = 0; c < 20; c++)	{		for (d = 0; d < 20 - c - 1; d++)		{			if (p[d] > p[d + 1])			{				int tmp = p[d];				p[d] = p[d + 1];				p[d + 1] = tmp;			}		}	}	cout << "正数为" << a << endl;	cout << "负数为" << b << endl;	cout << "均值为" << t << endl;	cout << "方差为" << f << endl;	cout << "从小到大输出为：" << endl;	for (i = 0; i < 20; i++)	{		cout << p[i] << ' ';	}	delete[]p;	system("pause");}